import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import axios from 'axios';

export default class Words extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');

        this.displayWords = this.displayWords.bind(this);
        this.onChangeWord = this.onChangeWord.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            token,
            words: [],
            newWord:''
        }
    }

    componentDidMount(e){
        if(this.state.token){
            axios.get('http://localhost:7000/word/words', {
                params: {
                token: this.state.token
                }})
            .then(res => this.setState({words: res.data})
            )
            .catch((error) => {
                console.log(error);
            })}
    }

    displayWords(e){
        return(
            this.state.words.map(word => {
                return (
                    <li className="list-group-item">
                        {word.word}
                        <button type="button" class="close" aria-label="Close" onClick={() => this.onClickDelete(word._id)}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </li>
                )
            })
        )

    }

    onClickDelete(wordId){
        axios.get('http://localhost:7000/word/delete', {
            params: {
              token: this.state.token,
              wordId: wordId
            }}).then(() => {
                console.log(res.data)
                window.location = '/words/'
            })
    }

    onChangeWord(e){
        this.setState({
            newWord: e.target.value
        })
    }

    onSubmit(e){
        e.preventDefault();
        const newWord = {
            token:this.state.token,
            word: this.state.newWord
        }
        axios.post('http://localhost:7000/word/newword', newWord)
        .then(res => {
            if (res.data.msg === 'success'){
                window.location = '/words/'
            }
        });
    }


    render() {
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (
            <div>
                <div> add css</div>
                <div className="row">
                    <div className="col">
                        <h1>Word Manager</h1>
                    </div>
                    <div className="col">
                    <div className="card text-white bg-primary" >
                    <div className="card-body">
                        <input 
                            className="form-control"
                            value={this.state.newWord}
                            onChange={this.onChangeWord}
                            />
                        <button type="submit" className="btn btn-secondary btn-block" onClick={this.onSubmit}>Add a word</button>
                    </div>
                    </div>
                    </div>
                </div>
                <div>
                    <ul>{ this.displayWords() }</ul>
                </div>
            </div>
        )
    }
}
