import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';

export default class Dashboard extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');

        let loggedIn = true;
        if(token !== 'connected' ){
            loggedIn = false;
        }
        this.state = {
            loggedIn
        }
    }

    onClickLogout(e){
        localStorage.setItem('token', null);
        window.location('/');
    }


    render(){
        if (!this.state.loggedIn){
            return <Redirect to='/users/login'/>
        }
        return(
            <div>
                <h1 className="mt-4">Dashboard</h1>
                <p className="lead mb-3">Welcome You</p>

                <button type="button" className="btn btn-primary btn-block">Login</button>
                <a href="/users/logout" className="btn btn-secondary" onClick={this.onClickLogout}>Logout</a>
                <a href="/newparty" className="btn btn-secondary">Create Party</a>
                <a href="/joinparty" className="btn btn-secondary">Join a Party</a>
            </div>
        )
    }
}
