import React, { Component } from 'react';
import { Redirect } from "react-router-dom";
import Footer from "../JSXaux/Footer"

export default class Ingame extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        const partyId = this.props.match.params.id;


        this.state = {
            token,
            partyId
        }
    }

    render() {
        var canvas = document.getElementById('cv');
        canvas.width = 400;
        canvas.height = 300;
        var context = canvas.getContext('2d');
        context.fillStyle = "black";
        context.font = "50px Arial";
        context.fillText('ASD', 0, 50);
        context.globalCompositeOperation = "destination-over";
        context.fillStyle = "#00FFFF";
        context.fillRect(0,0,canvas.width,canvas.height);//for white background
        context.globalCompositeOperation = "source-over";
        context.lineWidth = 2;
        context.strokeStyle="#FF0000";
        context.strokeRect(0, 0, canvas.width, canvas.height);


        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (

            <div className="row mt-5">
                <div className="col-md-4 m-auto">
                <template>
                    <div>
                        <canvas 
                        id = "gameCanvas"
                        ref="game" 
                        width="640" 
                        height = "480" 
                        style = {{border: "1px solid black"}}>
                        </canvas>
                        <p>
                        <input type="range" min="1" max="10" value="1" class="slider" id="lineWidth"/>>
                        <button >Clear</button>
                        <input type="color" id="lineColor"/>
                        </p>
                    </div>
                </template>
                </div>
                <div className="col-md-4 m-auto">
                    <Footer>
                    <div class="input-group">
                        <input class="form-control width100"/>
                        <span class="input-group-btn">
                            <button class="btn btn-info">Submit</button>
                        </span>
                    </div>
                    </Footer>
                </div>
                <div className="col-md-4 m-auto">
                    <div className="card card-body text-center">
                        TEST
                    </div>
                </div>
            </div>
        )
    }
}
