import React from "react";

import "./Ingame.css"
import MessageBox from './MessageBox/MessageBox'
import Messages from './Messages/Messages'
import PlayerList from './PlayerList/PlayerList'
import useGame from './_useGame'



const Chat = () => {
    const {messages, sendMessage, players, DrawingData} = useGame();

    function data() {
        return {
            context: {},
            DrawingData: DrawingData
        }
    }

    document.getElementById("gameCanvas")
    return (
        <div className="row">
        <div className="column left">
            <div className="row">
            <canvas 
                id = "gameCanvas"
                width="500" 
                height = "500"s
                style = {{border: "1px solid black"}}/>
            <p>
                <input type="range" min="1" max="10" className="custom-range" id="lineWidth"/>
                <button className="btn btn-primary">Clear</button>
                <input type="color" id="lineColor"/>
            </p>
            </div>
            <button className="btn btn-primary" onClick={() => window.location = '/dashboard/'}>Leave</button>
        </div>
        <div className="column middle">
            <div className="row" id='messageContainer'>
            <Messages messages = {messages} />
            </div>
            <div className="row">
            <MessageBox onSendMessage={ message => {
                sendMessage({message});
            }}/>
            </div>
        </div>
        <div className="column right">
            <PlayerList playerList={players} />
        </div>
        </div>
    );
}

export default Chat;
