import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import axios from 'axios';
import { contextUserData } from '../Context'
import jwt from 'jsonwebtoken';

import './addtop.css'

export default class Words extends Component {
    static contextType = contextUserData

    constructor(props){
        super(props);

        this.displayWords = this.displayWords.bind(this);
        this.onChangeWord = this.onChangeWord.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            token:'',
            words: [],
            newWord:''
        }
    }

    componentDidMount(e){
        const token = this.context.token;
        const data = jwt.decode(token);
        if(!data){
            console.log('HO!!')
            this.props.history.push("/users/loggin")
        }
        if(!data.isAdmin){
            this.props.history.push('/dashboard')
        }

        this.setState({token: token, isAdmin: data.isAdmin})

        if(this.state.token){
            axios.get('http://localhost:7000/word/words', {
                params: {
                token: this.state.token
                }})
            .then(res => this.setState({words: res.data})
            )
            .catch((error) => {
                console.log(error);
            })}
    }

    displayWords(e){
        return(
            this.state.words.map(word => {
                return (
                    <li className="list-group-item">
                        {word.word}
                        <button type="button" class="close" aria-label="Close" onClick={() => this.onClickDelete(word._id)}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </li>
                )
            })
        )

    }

    onClickDelete(wordId){
        axios.get('http://localhost:7000/word/delete', {
            params: {
              token: this.state.token,
              wordId: wordId
            }}).then(res => {
                console.log(res.data)
                window.location = '/words/'
            })
    }

    onChangeWord(e){
        this.setState({
            newWord: e.target.value
        })
    }

    onSubmit(e){
        e.preventDefault();
        const newWord = {
            token:this.state.token,
            word: this.state.newWord
        }
        if (newWord.word !==''){
            axios.post('http://localhost:7000/word/newword', newWord)
            .then(res => {
                if (res.data.msg === 'success'){
                    window.location = '/words/'
                }
            });
        }

    }


    render() {
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (
            <div className="addtop">                   
                    <div className="card text-white bg-primary" >
                    <div className="card-header text-center">
                        <h1>Word Manager</h1>
                    </div>
                    <div className="card-body">

                    <div className="row">
                        <div className="col">
                            <button 
                            type="submit" 
                            className="btn btn-secondary btn-block" 
                            onClick={()=> window.location= '/dashboard'}>
                                DashBoard
                            </button>
                        </div>
                        <div className="col">
                            <div className="input-group">
                                <input 
                                className="form-control"
                                value={this.state.newWord}
                                onChange={this.onChangeWord}
                                />
                                <button 
                                type="submit" 
                                className="btn btn-secondary" 
                                onClick={this.onSubmit}>
                                    Add a word
                                </button>
                            </div>
                        </div>
                    </div>
                    </div>
                </div>
                <div>
                    <ul>{ this.displayWords() }</ul>
                </div>
            </div>
        )
    }
}
