import React, { Component } from 'react';
import { Redirect } from "react-router-dom";
import Footer from "../JSXaux/Footer"
//import axios from 'axios';
import io from 'socket.io-client'
import useChat from "./_useChat";

export default class Ingame extends Component {
    constructor(props){
        super(props);
        const token = localStorage.getItem('token');
        const partyId = this.props.match.params.id;

        this.SocketManager = this.SocketManager.bind(this);

        const socket = io('http://localhost:7001/')
        const { messages, sendMessage } = useChat();


        this.state = {
            token,
            partyId,
            socket,
            messages,
            sendMessage
        }
    }

    componentDidMount(){
        const init = {
            partyId: this.state.partyId,
            token: this.state.token
        }
        this.state.socket.emit('newPlayer', init)

    }

    SocketManager(){
        const socket = io('http://localhost:7001/')
        socket.on('user-connected', name => console.log(name))
    }



    render() {
        const socket = io('http://localhost:7001/')
        socket.on('user-connected', name => console.log(name))

        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (
            <div className="row">
                <div className="col-6" style={{borderRight: '1px solid #ccc', borderLeft: '1px solid #ccc'}}>
                    <canvas 
                        id = "gameCanvas"
                        ref="game" 
                        width="480" 
                        height = "640" 
                        style = {{border: "1px solid black"}}>
                    </canvas>
                    <p>
                    <input type="range" min="1" max="10" value="1" class="slider" id="lineWidth"/>
                    <button >Clear</button>
                    <input type="color" id="lineColor"/>
                    </p>
                </div>
                <div className="col-4" style={{borderRight: '1px solid #ccc'}}>
                    <ul id="messages"></ul>
                    <Footer>
                        <div class="input-group">
                            <input class="form-control width100"/>
                            <span class="input-group-btn">
                                <button class="btn btn-info">Submit</button>
                            </span>
                        </div>
                    </Footer>
                </div>
                <div className="col" style={{borderRight: '1px solid #ccc'}}>
                    Players
                </div>
            </div>

        )
    }
}
