import React from "react";

import "./Ingame.css"
import MessageBox from './MessageBox/MessageBox'
import Messages from './Messages/Messages'
import useChat from './_useGame'


const Chat = () => {
    const {messages, sendMessage } = useChat();
    return (
        <div class="row">
        <div class="column left border-right border-left">
            Players:
        </div>
        <div class="column middle border-right">
            <MessageBox onSendMessage={ message => {
                sendMessage({message});
            }}/>
            <Messages messages = {messages} />
        </div>
        <div class="column right border-right">
            Players:
        </div>
        </div>
    );
}

export default Chat;
