import React, { Component } from 'react';
import axios from 'axios';

export default class Dashboard extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        this.newParty = this.newParty.bind(this);
        this.partyCards = this.partyCards.bind(this);

        this.state = {
            token,
            name: '',
            parties: [],
            curParty: []
        }
    }

    componentDidMount(e){

        axios.get('http://localhost:7000/dash', {
            params: {
              token: this.state.token
            }})
        .then(res => 
            this.setState({
            name: res.data.name,
            parties: res.data.parties,
            curParty: res.data.curParty
        }))
        .catch((error) => {
            console.log(error);
          })


    }

    onClickLogout(e){
        localStorage.setItem('token', null);
        window.location = '/';
    }

    newParty(e){
        axios.get('http://localhost:7000/newparty', {
            params: {
              token: this.state.token
            }})
        .then(res => 
            console.log(res.data)
        )
        .catch((error) => {
            console.log(error);
          })
    }

    partyCards(e){
        if (this.state.curParty.length>0){
            return (
                <div className="card bg-light mb-3" style="width: 20rem;">
                <div className="card-header">{this.state.curParty[0].creator.name}</div>
                <div className="card-body">
                    <p className="card-text">
                        created a party on 
                        {this.state.curParty[0].party.date.getDate()}/{this.state.curParty[0].party.date.getMonth()+1}/{this.state.curParty[0].party.date.getFullYear()}
                    </p>
                </div>
                </div>
            )
        }
    }

    render(){
        return(
            <div>
                <h1 className="mt-4">Dashboard</h1>
        <p className="lead mb-3">Welcome {this.state.name}</p>
                { this.curPartyCard() }
                <button type="button" className="btn btn-primary btn-block" onClick={this.onClickLogout}>Logout</button>
                <button type="button" className="btn btn-primary btn-block" onClick={this.newParty}>New Party</button>
                <a href="/newparty" className="btn btn-secondary">Create Party</a>
                <a href="/joinparty" className="btn btn-secondary">Join a Party</a>
            </div>
        )
    }
}
