import React, { Component } from 'react';
import { Redirect } from "react-router-dom";

export default class Ingame extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        const partyId = this.props.match.params.id;


        this.state = {
            token,
            partyId
        }
    }

    render() {
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (

            <div>
                <head>
                    <title>Socket.IO chat</title>
                    <style>
                        * { {margin: '0', padding: '0', boxSizing: 'border-box'} }
                        body { {font: "13px Helvetica, Arial"} }
                        .zone_saisie { {background: "#000", padding: '3px', position: 'fixed', bottom: '0', width: '100%'} }
                        .zone_saisie input { {border: '0', padding: '10px', width: '90%', marginRight: '.5%'} }
                        .zone_saisie button { {width: '9%', background: 'rgb(130, 224, 255)', border: 'none', padding: '10px'} }
                        #messages { {listStyleType: 'none', margin: '0', padding: '0'} }
                        #messages li { {padding: '5px 10px'} }
                        #messages li:nth-child(odd) { {background: '#eee'} }
                    </style>
                </head>
                <body>
                    <div>
                    <ul id="messages"></ul>
                    <div className="zone_saisie">
                        <input id="m" /> <button>Send</button>
                    </div>

                    </div>

                </body>
            </div>
        )
    }
}
