import {useEffect, useRef, useState} from "react";
import socketIOClient from "socket.io-client";

const useChat = () => {
    const [messages, setMessages] = useState([]);
    const socketRef = useRef();
    const token = localStorage.getItem('token');
    const partyID = localStorage.getItem('partyID');


    useEffect(() => {
        socketRef.current = socketIOClient('http://localhost:7001');

        socketRef.current.emit("newPlayer", {token, partyID})

        socketRef.current.on("newChatMessage", ({ message })=> {
            setMessages(messages => [...messages, message])
        });

        return () => {
            socketRef.current.emit("disconnectionParams", {token, partyID});
            setTimeout(() => {
            }, 4000);
            socketRef.current.disconnect();

        };
    }, [token, partyID])

    const sendMessage = ({message}) => {
        console.log(message)
        socketRef.current.emit("newChatMessage", {message, token, partyID})
    }

    return {messages, sendMessage};
}

export default useChat;
