import React, { Component } from 'react';
import axios from 'axios';

export default class Dashboard extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        this.onClickParty = this.onClickParty.bind(this);

        this.state = {
            token,
            name: '',
            parties: [],
            curParty: []
        }
    }

    onClickParty(e){
        console.log(this.state.token)
        axios.get('http://localhost:7000/dash', {
            params: {
              token: this.state.token
            }})
        .then(res => console.log(res))
        .catch((error) => {
            console.log(error);
          })

        /* axios.get('http://localhost:7000/dashboard')
        .then(response => {console.log(response);

            this.setState({
                name: response.data.name,
                parties: response.data.parties,
                curParty: response.data.curParty
            })
            console.log(this.state); 
        })
        .catch((error) => {
            console.log(error);
          }) */
    }

    onClickLogout(e){
        localStorage.setItem('token', null);
        window.location = '/';
    }


    render(){
        return(
            <div>
                <h1 className="mt-4">Dashboard</h1>
                <p className="lead mb-3">Welcome You</p>

                <button type="button" className="btn btn-primary btn-block" onClick={this.onClickLogout}>Logout</button>
                <button type="button" className="btn btn-primary btn-block" onClick={this.onClickParty}>get Party</button>
                <a href="/newparty" className="btn btn-secondary">Create Party</a>
                <a href="/joinparty" className="btn btn-secondary">Join a Party</a>
            </div>
        )
    }
}
