import React, { Component } from 'react';
import axios from 'axios';
import { contextUserData } from '../Context'

export default class Lobby extends Component {
    static contextType = contextUserData

    constructor(props){
        super(props);

        this.dashboard = this.dashboard.bind(this)

        this.state = {
            token: '',
            name: '',
            parties: [],
        }
    }

    componentDidMount(e){
        const token = this.context.token;
        if(!token){
            this.context.setFlashMsg({type: "warning", msg: "Please log in to access this content"})
            this.props.history.push("/users/loggin")
        }
        this.setState({token: token})

        axios.get('http://localhost:7000/lobby', {
            params: {
            token: token
            }})
        .then(res => {
            this.setState({
            parties: res.data.parties,
            });
        })
        .catch((error) => {
            console.log(error);
        })


    }

    partyList(e){
        return (
            this.state.parties.map(party => {
            console.log(party._id)
            return (
                <div className="card bg-light mb-3" style={{width: '20rem'}}>
                    <div className="card-header">{party.creator.name}</div>
                    <div className="card-body">
                        <p className="card-text">
                            {'created a party on '+party.date.slice(0,10)}
                        </p>
                        <button type="button" className="btn btn-primary btn-block" onClick={() => this.onClickJoin(e,party._id)}>Join Game</button>
                    </div>
                </div>
            )
        }))
    }

    onClickJoin(e, partyID){
        this.context.setGameID(partyID);
        localStorage.setItem('partyID', partyID);
        console.log(localStorage.getItem('partyID'));
        this.props.history.push("/ingame")
    }

    numberParty(e){
        var n = this.state.parties.length
        var sentence = ''
        if (n===1|| n===0){
            sentence = n.toString() + ' game'
        } else {
            sentence = n.toString() + ' games'
        }
        return <p>There are currently {sentence} available</p>
    }

    onClickLogout(e){
        this.context.setToken('');
        this.props.history.push("/");
    }

    dashboard(e){
        this.props.history.push("/dashboard")
    }

    render() {

        return (
            <div>
                <h1 className="mt-4">Pictionary Lobby</h1>
                <p className="lead mb-3">Game Lobby, pick a game</p>
                { this.numberParty() }
                { this.partyList() }
                <button type="button" className="btn btn-secondary btn-block" onClick={this.dashboard}>Dashboard</button>
                <button type="button" className="btn btn-primary btn-block" onClick={this.onClickLogout}>Logout</button>
            </div>
        )
    }
}
