import React, { Component } from 'react'
import jwt from 'jsonwebtoken';
import { Redirect } from 'react-router-dom';
import axios from 'axios';

export default class Words extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        var data = jwt.decode(token);

        this.displaysWords = this.displaysWords.bind(this);

        this.state = {
            token,
            isAdmin: data.isAdmin,
            words: [],
            newWord:''
        }
    }

    componentDidMount(e){
        if(this.state.token){
            axios.get('http://localhost:7000/word/words', {
                params: {
                token: this.state.token
                }})
            .then(res => this.setState({words: res.data})
            )
            .catch((error) => {
                console.log(error);
            })}
    }

    displaysWords(){
        this.state.words.map(word => {
            return <li class="list-group-item">{word}</li>
        })
    }

    onChangeWord(e){
        this.setState({
            email: e.target.value
        })
    }

    render() {
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (
            <div>
                <div> add css</div>
                <div className="row">
                    <div className="col">
                        <h1>Word Manager</h1>
                    </div>
                    <div className="col">
                    <div className="card text-white bg-primary" >
                    <div className="card-body">
                        <input 
                            className="form-control"
                            value={this.state.newWord}
                            onChange={this.onChangeWord}
                            />
                        <button type="button" className="btn btn-secondary btn-block" onClick={() => window.location = '/words/'}>Add a word</button>
                    </div>
                    </div>
                    </div>
                </div>
                <p>{ this.displaysWords() }</p>
            </div>
        )
    }
}
