import React, { Component } from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import jwt from 'jsonwebtoken';

export default class Dashboard extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');

        this.newParty = this.newParty.bind(this);
        this.partyCards = this.partyCards.bind(this);
        this.numberParty = this.numberParty.bind(this);

        this.state = {
            token,
            name: '',
            parties: [],
            curParty: []
        }
    }

    componentDidMount(e){
        if(this.state.token){
            axios.get('http://localhost:7000/dash', {
                params: {
                token: this.state.token
                }})
            .then(res => 
                this.setState({
                name: res.data.name,
                parties: res.data.parties
            }))
            .catch((error) => {
                console.log(error);
            })}


    }

    onClickLogout(e){
        localStorage.setItem('token', '');
        window.location = '/';
    }

    newParty(e){
        axios.get('http://localhost:7000/newparty', {
            params: {
              token: this.state.token
            }})
        .then(res => {
            localStorage.setItem('partyID', res.data.partyID);
            window.location = '/ingame/'
        }
        )
        .catch((error) => {
            console.log(error);
          })
    }

    Lobby(e){
        window.location = '/lobby'
    }

    partyCards(e){
        var n=0;
        return (

            this.state.parties.map(party => {
            return (
                <div class="col-sm-6">
                <div className="card bg-light mb-3" style={{width: '20rem'}}>
                    <div className="card-header">
                        {party.creator.name}
                        <button type="button" class="close" aria-label="Close" onClick={() => this.onClickDelete(e,party._id)}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="card-body">
                        <p className="card-text">
                            {'created a party on '+party.date.slice(0,10)}
                        </p>
                        {party.isEnded ? '' : <button type="button" className="btn btn-primary btn-block" onClick={() => this.onClickJoin(e,party._id)}>Join Game</button>}
                    </div>
                </div>
                </div>
            )}
        ))
    }

    onClickJoin(e, partyID){
        localStorage.setItem('partyID', partyID);
        console.log(localStorage.getItem('partyID'));
        window.location = '/ingame/'
    }

    onClickDelete(e,partyID){
        axios.get('http://localhost:7000/delete', {
            params: {
              token: this.state.token,
              partyID: partyID
            }}).then(() => {
                console.log('TEST')
                window.location = '/dashboard/'
            })
    }

    numberParty(e){
        var n = this.state.parties.length
        var token = this.state.token;
        var data = jwt.decode(token);

        var sentence = ''

        if (data.isAdmin){
            if (n===1|| n===0){
                sentence = 'is '+ n.toString() + ' party'
            } else {
                sentence = 'are '+ n.toString() + ' parties'
            }
            return <p> There {sentence} in the database </p>
        } else {
            if (n===1|| n===0){
                sentence = n.toString() + ' party'
            } else {
                sentence = n.toString() + ' parties'
            }
            return <p>You have created {sentence} </p>
        }

    }

    render(){
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return(
            <div className="container">
                <h1 className="mt-4">Dashboard</h1>
                <p className="lead mb-3">Welcome {this.state.name}</p>
                <p>{ this.numberParty() }</p>
                <p>{ this.partyCards() }</p>
                <button type="button" className="btn btn-info btn-block" onClick={this.newParty}>New Party</button>
                <button type="button" className="btn btn-success btn-block" onClick={this.Lobby}>Join a Party</button>
                <button type="button" className="btn btn-primary btn-block" onClick={this.onClickLogout}>Logout</button>
            </div>
        )
    }
}
