import React, { useState, useEffect, Component } from "react";
import io from "socket.io-client";
import { Redirect } from "react-router-dom";
import Footer from "../JSXaux/Footer"

let socket;

const IngameSocket = ({ location }) => {


    const token = localStorage.getItem('token');
    const partyId = localStorage.getItem('partyID');

    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [users, setUsers] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const SocketServer = 'http://localhost:7001/';

    function appendMessage(message) {
        const messageContainer = document.getElementById('message-container')
        const messageElement = document.createElement('div')
        messageElement.innerText = message
        messageContainer.append(messageElement)
    }

    useEffect(() => {
        socket = io(SocketServer);
        const init = {
            partyId: partyId,
            token: token
        }
        socket.emit('newPlayer', init)
        socket.on('userConnected', rouge => {
            console.log(rouge);
            appendMessage(rouge + ' is connected')
            })
    })



    if (!token){
        return (<Redirect to="/users/login"/>)
    }
    return (
        <div className="row">
            <div className="col-6" style={{borderRight: '1px solid #ccc', borderLeft: '1px solid #ccc'}}>
            <canvas 
                    id = "gameCanvas"
                    width="480"
                    height = "640" 
                    style = {{border: "1px solid black"}}>
                </canvas>
                <p>
                <input type="range" min="1" max="10" value="1" class="slider" id="lineWidth"/>
                <button >Clear</button>
                <input type="color" id="lineColor"/>
                </p>
            </div>
            <div className="col-4" style={{borderRight: '1px solid #ccc'}}>
                <div id="message-container"></div>
                <Footer>
                    <div class="input-group">
                        <input class="form-control width100"/>
                        <span class="input-group-btn">
                            <button class="btn btn-info">Submit</button>
                        </span>
                    </div>
                </Footer>
            </div>
            <div className="col" style={{borderRight: '1px solid #ccc'}}>
                Players
            </div>
        </div>

    )

};

export default IngameSocket
