import React, { Component } from 'react'
import jwt from 'jsonwebtoken';
import { Redirect } from 'react-router-dom';
import axios from 'axios';

export default class Words extends Component {
    constructor(props){
        super(props);

        const token = localStorage.getItem('token');
        var data = jwt.decode(token);

        this.state = {
            token,
            isAdmin: data.isAdmin,
            words: []
        }
    }

    componentDidMount(e){
        if(this.state.token){
            axios.get('http://localhost:7000/word/words', {
                params: {
                token: this.state.token
                }})
            .then(res => this.setState({words: res.data})
            )
            .catch((error) => {
                console.log(error);
            })}
    }

    displaysWords(){
        this.state.words.map(word => {
            return <li class="list-group-item">{word}</li>
        })
    }

    render() {
        if (!this.state.token){
            return (<Redirect to="/users/login"/>)
        }
        return (
            <div>
                <div> </div>
                <div className="row">
                    <div className="col">
                        <h1>Word Manager</h1>
                    </div>
                    <div className="col">
                        <button type="button" className="btn btn-primary btn-block" onClick={() => window.location = '/words/'}>Add Word</button>
                    </div>
                </div>
                <p>{ this.displaysWords() }</p>
            </div>
        )
    }
}
